import * as types from './actionTypes';

export function receiveApps(json){
  return {type: types.RECEIVE_APPS, apps: json};
}

export const fetchApplications = () => {
  return (dispatch, getState) => {
    return fetch('http://phobos.apple.com/WebObjects/MZStoreServices.woa/ws/RSS/toppaidapplications/limit=100/json',{
      method: "GET"
    }).then(response => {
      let json = response.json();
      if (response.status === 200){
        dispatch(receiveApps(response.data))
      }
    })
  }
}



