import * as types from './actionTypes';

export function receiveApps(json){
  return {type: types.RECEIVE_APPS, apps: json};
}

export function fetchApps() {
    return (dispatch) => {
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(response =>
                response.json().then(data => ({
                    data: data,
                    status: response.status
                }))
            )
            .then(response => {
                if(response.status === 200){
                    dispatch(receiveApps(response.data))
                }else{
                    var flash = {
                        type: 'error',
                        title: 'Error getting task list',
                        content: 'There was an error getting the task list. Please try again.'
                    }
                    dispatch({type: "DISPLAY_FLASH", data: flash})
                }
            });
    };
}

export function fetchApplications() {
  return (dispatch) => {
    return fetch('https://jsonplaceholder.typicode.com/users',{
      method: "GET",
      headers: {
          "Content-Type": "application/json"
      }
    }).then(response => {
      let json = response.json();
      if (response.status === 200){
        dispatch(receiveApps(response.data))
      }
    })
  }
}

export const fetchData = () => {
    return (dispatch) => {
        dispatch(loadingData())
        return fetch(BLL.helpers.globalVars.API_BASE_URL + '/asset/GetReportData', {
            method: "GET",
            credentials: 'include',
            headers: {
                "Content-Type": "application/json"
            }
        }).then(response => BLL.helpers.miscHelpers.responseValidator(response)).then(response => {
            if (response.success) {
                dispatch(initReports(response.payload))
            } else {
                dispatch(initReports([]))
                toastr.error('Error loading report data', response.messages[0]);
            }
        })
    }
}
