import * as types from './actionTypes';

export function receiveApps(json){
  return {type: types.RECEIVE_APPS, apps: json};
}

export const fetchApplications = () => {
  return (dispatch, getState) => {
    return fetch('http://time.jsontest.com').then(response => {
      let json = response.json();
      if (response.status === 200){
        dispatch(receiveApps(response.data))
      }
    })
  }
}


export function fetchStuff() {
    return (dispatch) => {
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(response =>
                response.json().then(data => ({
                    data: data,
                    status: response.status
                }))
            )
            .then(response => {
                if(response.status === 200){
                    dispatch(receiveStuff(response.data))
                }else{
                    var flash = {
                        type: 'error',
                        title: 'Error getting task list',
                        content: 'There was an error getting the task list. Please try again.'
                    }
                    dispatch({type: "DISPLAY_FLASH", data: flash})
                }
            });
    };
}


